mapper.insert:
/*상위 카테고리 등록, 이양원*/
INSERT  /*topCateReg*/
    INTO TOPCATEGORY(
		  NAME
		, REGDATE
		, LAST_MODI_YMD
	)
	VALUES(
		  #{TOPCATEGORY_ID}
		, SYSDATE()
		, SYSDATE()
	)

/*하위 카테고리 등록, 이양원*/
INSERT  /*subCateReg*/
	INTO SUBCATEGORY(
		  TOPCATEGORY_ID
		, NAME
		, REGDATE
		, LAST_MODI_YMD
	)
	VALUES(
		  #{TOPCATEGORY_ID}
		, #{SUB_NM}
		, SYSDATE()
		, SYSDATE()
	)

/*주류 정보 등록, 이양원*/
INSERT   /*alcoholReg*/
	INTO ALCOHOL(
		  SUBCATEGORY_ID
		, NAME
		, DEGREE
		, DETAIL
		, ALC_IMG_ID
		, REGDATE
		, LAST_MODI_YMD
	)
	VALUES(
		  #{SUBCATEGORY_ID}
		, #{ALCOHOL_NM}
		, #{ALC_DEGREE}
		, #{ALC_DETAIL}

		, #{ALC_IMG_ID}
		, SYSDATE()
		, SYSDATE()
	)

mapper.delete:
/*주류 정보 삭제, 이양원*/
 DELETE FROM ALCOHOL  /*alcoholDel*/
 WHERE 1=1
     AND ALCOHOL_ID = #{ALCOHOL_ID}
